sition: fixed; top: -99999; left: -99999; display: none; width: 0; height: 0; -moz-transform: scale(0, 0); -ms-transform: scale(0, 0); -webkit-transform: scale(0, 0); transform: scale(0, 0); filter: progid:DXImageTransform.Microsoft.Alpha(Opacity=0); opacity: 0; }input:checked + label { width: auto; font-size: 13px; }input:checked + label:after { content: ""; background-color: rgba(255, 255, 255, 0); }label { position: relative; display: inline-block; width: 20px; margin-right: 2px; font-size: 0; text-decoration: none; overflow: hidden; white-space: nowrap; cursor: pointer; color: #646464; border-bottom: 1px dashed rgba(100, 100, 100, 0.4); }label:after { content: ".../"; position: absolute; left: 0; top: 0; display: inline-block; width: 100%; height: 100%; font-size: 13px; text-align: center; -moz-transition: background-color 0.3s; -o-transition: background-color 0.3s; -webkit-transition: background-color 0.3s; transition: background-color 0.3s; }span { display: inline-block; width: 100%; margin-top: 8px; margin-bottom: 16px; padding-right: 32px; text-align: right; font-size: 12px; color: #a0a0a0; filter: progid:DXImageTransform.Microsoft.Alpha(Opacity=85); opacity: 0.85; }a { display: inline; text-decoration: none; color: #699df4; border-bottom: 1px dashed rgba(70, 136, 241, 0.4); -moz-transition: color 0.3s, border-bottom 0.3s; -o-transition: color 0.3s, border-bottom 0.3s; -webkit-transition: color 0.3s, border-bottom 0.3s; transition: color 0.3s, border-bottom 0.3s; }a:hover { color: #3e7ee8; border-bottom-color: rgba(70, 136, 241, 0.8); }</style>
