    <main>
      <h1>糟糕，好像發生些麻煩了..</h1>
<?php if ($messages) { ?>
        <article data-title='錯誤訊息'>
    <?php echo implode('', array_map(function($message) { return '<blockquote><pre>' . $message . '</pre></blockquote>'; }, $messages));?>
        </article>
<?php }
      if ($details) { ?>
        <article data-title='錯誤資訊'>
          <table>
            <tbody>
        <?php echo implode('', array_map(function($detail) { return '<tr><th>' . $detail['title'] . '</th><td>' . $detail['content'] . '</td></tr>'; }, $details));?>
            </tbody>
          </table>
        </article>
<?php }
      if ($count = count($traces)) { ?>
        <article data-title='流程追蹤'>
          <table>
            <thead><tr><th width="50" class="c">順序</th><th width="250">路徑</th><th>資訊</th></tr></thead>
            <tbody>
        <?php echo implode('', array_map(function($trace) use ($count) {
                $dir  = pathinfo($trace['path'], PATHINFO_DIRNAME);
                $base = pathinfo($trace['path'], PATHINFO_BASENAME);
                $return = '';
                $return .= '<tr>';
                $return .= '<td class="c">' . ($count - $trace['index']) . '</td>';
                $return .= '<td>' . ($dir && $dir != '.'  ? '<input type="checkbox" id="a' . $trace['index'] . '"><label for="a' . $trace['index'] . '">' . $dir . DIRECTORY_SEPARATOR . '</label>' : '') . $base . '</td>';
                $return .= '<td>' . $trace['info'] . '</td>';
                return $return;
              }, $traces));?>

            </tbody>
          </table>
        </article>
<?php } ?>
      <span>©2014 - <?php echo date('Y');?> <a href='https://www.ioa.tw/' target='_blank'>OA Wu</a>, All Rights Reserved.</span>
